import axios from "axios";

const SUB_URL = "http://localhost:5050/api/getsubjects/"
//const SUB_URL = "https://subject-4dcnj7fm6a-uc.a.run.app/api/getsubjects/"

let axiosConfig = {
    headers: {
        "Content-Type": "application/json",
        "Access-Control-Allow-Origin": "http://localhost:3000"
    }
}


const allSubjects = async () => {
    const response = await fetch(SUB_URL + "all", axiosConfig);
    const data = await response.json();
    localStorage.setItem("subjects", JSON.stringify(data));
    return data;
};

const getAllSubjects = () => {
    return JSON.parse(localStorage.getItem("subjects"));
};


const SubjectServices = {
    allSubjects,
    getAllSubjects
}


export default SubjectServices;
